INSERT INTO preamble_templates VALUES (1, 'Standard Hierarchical', 'Standard composition: Universal → State → Page Type → Project', '# UNIVERSAL COMPLIANCE PRINCIPLES\n{{ universal_preamble }}\n\n# STATE-SPECIFIC REQUIREMENTS: {{ state_code }}\n{{ state_preamble }}\n\n# PAGE TYPE GUIDANCE: {{ page_type }}\n{{ page_type_preamble }}\n\n{% if project_preamble %}\n# PROJECT-SPECIFIC CONTEXT\n{{ project_preamble }}\n{% endif %}\n\n# CITATION FORMAT REQUIREMENTS\nCRITICAL: Format all citations as structured JSON:\n{\n  "violations": [\n    {\n      "statute": "Full statute reference",\n      "rule": "Plain language requirement",\n      "severity": "critical|important|minor",\n      "location": "Where violation occurs",\n      "evidence": "Specific violating content",\n      "recommendation": "How to fix"\n    }\n  ]\n}', 1, '2025-10-26 22:10:33', '2025-10-26 22:10:33');
